import { useState } from "react";
import './Login.css';

function Login(){

    const [username,setUsername]=useState("");
    const [password,setPassword] = useState("");

    const login=(event)=>{
        event.preventDefault()
        if(password.length>0 && username.length>0){
            alert("Logged in..")
        }
        else{
            alert("invalid")
        }

    }
    return(
        <div class="login">
            <form onSubmit={login}>
                <div class="username">
                    <input class="form-control" type="text" placeholder="UserName" onChange={(e)=>setUsername(e.target.value)} value={username}></input>
                </div>
                <div class="password">
                    <input class="form-control" type="password" placeholder="Password" onChange={(e)=>setPassword(e.target.value)} value={password}></input>
                </div>
                <button class="btn btn-primary" type="submit">Login</button>
            </form>
        </div>
    )
}
export default Login;
