import { useState } from "react";
import './Login.css';
import axios from "axios";

function Login(){

    const [username,setUsername]=useState("");
    const [password,setPassword] = useState("");

    const login=(event)=>{
        event.preventDefault()
        axios.post("http://localhost:5134/api/controller/login",{
            username: username,
            password: password
        }).then((response)=>{
            console.log(response.data)
            alert("Log In Successfull")
        }).catch((err)=>{
            console.log(err)
            alert(err.response.data)
        })
        if(password.length>0 && username.length>0){
            alert("Logged in..")
        }
        else{
            alert("invalid")
        }

    }
    return(
        <div class="login">
            <h4 class="title">Login</h4>
            <form onSubmit={login}>
                <div class="username space">
                    <input class="form-control" type="text" placeholder="Username" onChange={(e)=>setUsername(e.target.value)} value={username}></input>
                </div>
                <div class="password space">
                    <input class="form-control" type="password" placeholder="Password" onChange={(e)=>setPassword(e.target.value)} value={password}></input>
                </div>
                <div class="space">
                    <button class="btn space btn-primary" type="submit">Login</button>
                </div>
            </form>
        </div>
    )
}
export default Login;
