import { useState } from "react";
import './Login.css';
import axios from "axios";

function Login(){

    const [username,setUsername]=useState("");
    const [password,setPassword] = useState("");

    const login=(event)=>{
        event.preventDefault()
        axios.post("http://localhost:5134/api/controller/login",{
            userName: username,
            password: password
        }).then((response)=>{
            console.log(response.data)
            alert("Log In Successfull")
        }).catch((err)=>{
            console.log(err)
            alert(err.response.data)
        })

    }
    return(
        <div>
            <h3>Design Studio Courses</h3>
            <div class="p-3 bg-white shadow rounded-lg flex items-center justify-center m-40 h-60">
            <h4 class="font-semibold text-slate-900">Login</h4>
                <form onSubmit={login}>
                    <div class="username space">
                        <input required class="focus:ring-2 focus:ring-blue-500 focus:outline-none appearance-none w-full text-sm leading-6 text-slate-900 placeholder-slate-400 rounded-md py-1 pl-2 ring-2 ring-slate-200 shadow-sm" type="text" placeholder="Username" onChange={(e)=>setUsername(e.target.value)} value={username}></input>
                    </div>
                    <div class="password space">
                        <input required class="focus:ring-2 focus:ring-blue-500 focus:outline-none appearance-none w-full text-sm leading-6 text-slate-900 placeholder-slate-400 rounded-md py-1 pl-2 ring-2 ring-slate-200 shadow-sm" type="password" placeholder="Password" onChange={(e)=>setPassword(e.target.value)} value={password}></input>
                    </div>
                    <div class="space">
                        <button class="h-10 px-6 font-semibold rounded-full border border-blue-500 text-slate-900" type="submit">Login</button>
                    </div>
                </form>
            </div>
        </div>
    )
}
export default Login;
